INSERT INTO `jc_microblog` VALUES ('13', '北京赢了！电力护航，2022看我们的！', '', '<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/27102420u3zz.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/271024443j2l.jpg\" /></p>\r\n', '/Jeedw/u/cms/www/201601/271024443j2l.jpg', '1', '1', '2015-07-31 18:38:00', '国网北京电力', '2015-08-14 13:57:21', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('14', '【新人新语之一】', '', '<p>#媒体新人看电力#<span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\">【新人新语之一】你知不知道有这样一群人，他们四点半就已经开始工作了。在丰台区苇子坑路线路施工改造现场，电力师傅们爬上爬下，安装开关断路器，更换高损耗变压器，改造紧凑型电缆。烈日炎炎，他们汗流浃背，但直到中午，才顾得上休息会喝口水。为了尽快完工，师傅们也是蛮拼的哦~</span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/27102953ouh6.jpg\" /></span></p>\r\n', '/Jeedw/u/cms/www/201601/27102953ouh6.jpg', '1', '1', '2015-08-02 20:52:00', '国网北京电力', '2015-08-14 13:56:54', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('15', '【新人新语之二】', '', '<div class=\"WB_text W_f14\" node-type=\"feed_list_content\" style=\"padding-bottom: 3px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 16px; font: 14px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">#媒体新人看电力#【新人新语之二】炎炎夏日苇子坑，电力工人作业中；老旧线路隐患多，刮风下雨难保障；变电措施不能少，安全保护最重要；入夏用电高峰期，开关断路须安装；工人每日早早起，四点三十已出发；汗流浃背如雨下，再苦再累也不怕；整改线路为北京，首都多增一抹亮。</div>\r\n<div class=\"WB_feed_expand\" style=\"padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 12px/15px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-width: 0px\">\r\n	<div class=\"W_arrow_bor W_arrow_bor_t\" style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; height: 10px; overflow: hidden; padding-top: 0px\">&nbsp;</div>\r\n	<div class=\"WB_expand S_bg1\" node-type=\"feed_list_forwardContent\" style=\"padding-bottom: 13px; background-color: rgb(242,242,245); margin: 0px 0px 10px; padding-left: 16px; padding-right: 16px; padding-top: 10px\">\r\n		<div class=\"WB_info\" style=\"padding-bottom: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; padding-top: 5px\">@国网北京市电力公司</div>\r\n		<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; line-height: 23px; margin: 0px; padding-left: 0px; padding-right: 0px; word-wrap: break-word; overflow: hidden; padding-top: 1px\">#媒体新人看电力#【新人新语之一】你知不知道有这样一群人，他们四点半就已经开始工作了。在丰台区苇子坑路线路施工改造现场，电力师傅们爬上爬下，安装开关断路器，更换高损耗变压器，改造紧凑型电缆。烈日炎炎，他们汗流浃背，但直到中午，才顾得上休息会喝口水。为了尽快完工，师傅们也是蛮拼的哦~</div>\r\n	</div>\r\n</div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/271034204qcd.jpg\" /></p>\r\n', '', '1', '1', '2015-08-02 20:53:00', '国网北京电力', '2015-08-02 20:53:00', '', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('16', '电力男神来了！', '', '<p>#电力男神来了！#<span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\">8月3号是什么日子？再问这个您就OUT啦</span><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\">趁着&ldquo;男神节&rdquo;驾到，也来晒一晒我们的&ldquo;电力男神&rdquo;。他们平时扎根一线，低调内敛，走的是技术控路线，是大家眼中城市可靠供电的守护者。但同时，小编觉得工作中的他们自带男神光环。来，看看咱电力员工的工作范儿，你有没有被电到的感觉？</span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/27105135jemt.jpg\" /></span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/271051521ehm.jpg\" /></span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 14px/23px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201601/27105203rcm9.jpg\" /></span></p>\r\n<p><span style=\"text-transform: no