INSERT INTO `jc_microblog` VALUES ('69', '除了精彩赛事 田径世锦赛还有啥', '', '<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22095954sp4a.jpg\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 28pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; font: 13.63px/23.63px Arial, \'Microsoft YaHei\'; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-width: 0px\"><span style=\"font-family: 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; font-size: 14pt\">世界顶级田径赛事&mdash;&mdash;田径世锦赛来到北京，田径粉丝们动起了吧，这里有最棒的场馆，最牛的运动员，最酷的比赛，还有最靠谱的电力供应和最贴心的服务。</span></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 28.1pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><span style=\"text-transform: none; background-color: rgb(255,255,255); font-variant: normal; font-style: normal; text-indent: 0px; font-family: 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); font-size: 14pt; font-weight: normal; word-spacing: 0px; -webkit-text-stroke-width: 0px\"><b style=\"word-wrap: break-word !important; font-weight: bold\"><span style=\"font-family: 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; color: rgb(255,102,0); font-size: 14pt\">大赛来了，我们先到了&mdash;&mdash;电力照亮比赛场。</span></b></span></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 28.1pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/221000222833.jpg\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; font: 19.09px/30.9px 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-width: 0px\"><span style=\"font-family: 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; font-size: 14pt\">光，照亮赛场；电，让赛事动力十足，直播、照明、记录&hellip;&hellip;电让赛场闪耀给全世界。赛场群星闪耀，绚丽的灯光点亮了赛场天空，赛场外，电力保障和服务始终守候在这里。</span></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; font: 14pt/30.9px 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><b style=\"word-wrap: break-word !important; font-weight: bold\"><span style=\"font-family: 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; color: rgb(255,102,0); font-size: 14pt\">我在鸟巢之外<span style=\"max-width: 100%; word-wrap: break-word !important\">15</span>米守候你&mdash;&mdash;营业厅。用电咨询、购电、修补电卡&hellip;&hellip;都有的。（博尔特跑到这里只要<span style=\"max-width: 100%; word-wrap: break-word !important\">1.4</span>秒呦）。</span></b></span></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22100050z5ys.jpg\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 19.09px/30.9px 仿宋_GB2312; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><span class=\"Apple-converted-space\">&nbsp;</span></span><b style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; font: bold 19.09px/30.9px 仿宋_GB2312; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><span style=\"font-family: 仿宋_GB2312; max-width: 100%; word-wrap: break-word !important; color: rgb(255,102,0); font-size: 14pt\">如果您开电动车来看比赛，充电桩在这里呦。</span></b></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22100115ub9t.jpg\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22100141e6f9.jpg\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22100156mmci.jpg\" /></p>\r\n<p style=\"text-align: left; padding-bottom: 0px; text-indent: 27.75pt; margin: 0cm 0cm 0pt; padding-left: 0px; padding-right: 0px; max-width: 100%; word-wrap: break-word !important; height: auto !important; padding-top: 0px\"><span style=\