INSERT INTO `jc_microblog` VALUES ('72', '很温暖，很有爱~~', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国网北京房山供电公司</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">小鸟说早早早，你为什么背上小书包，我要上学校&hellip;&hellip;有木有跟着唱出来？在新学期来临之际，电力&ldquo;红马甲&rdquo;助学圆梦顺利启程。他们冒着酷暑为民仁小学改造线路并为那里的20位困难小学生送去了崭新的小书包。是不是很有爱啊！如果你也想献一份爱心，请点赞哦！<span class=\"Apple-converted-space\">&nbsp;</span>@国网北京市电力公司</div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101108fd8l.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/221011267ggh.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/221011422tys.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101154txqa.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/221012077g31.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101220l97y.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101232veat.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101243jtfj.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101257rqa3.jpg\" /></p>\r\n', '/Jeedw/u/cms/www/201508/22101257rqa3.jpg', '1', '1', '2015-08-22 10:13:19', '国网北京电力', '2015-08-22 10:13:19', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('73', '#世锦赛保电时刻#', '', '<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 13.63px/22.99px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\">【 梦想启幕 精彩绽放】今天，2015年北京国际田联世界田径锦标赛在鸟巢隆重开幕。这是继奥运会后，北京迎来的最重要的世界级体育盛会。国网北京市电力公司安排保障人员1431人，车辆141辆，全方位开展保电工作，为大赛开幕提供了高标准高质量的电力保障，圆满完成当日保电任务。</span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 13.63px/22.99px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101630imv1.jpg\" /></span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 13.63px/22.99px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/221016443kyp.jpg\" /></span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 13.63px/22.99px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201508/22101713evrb.jpg\" /></span></p>\r\n<p><span style=\"text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; display: inline !important; font: 13.63px/22.99px Arial, \'Microsoft YaHei\'; white-space: normal; float: none; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www