INSERT INTO `jc_microblog` VALUES ('138', '//@国家电网: #电网人# 老李工作44年，靠着精打细算，为单位节省经费100多万元。节俭一时并不难，难能可贵的是这份', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国网河北电力</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">#国家电网电力达人#老李李彦明，是国家电网河北高碑店市供电公司的一名普通职工。在电力局，人人都说老李是财迷，啥&ldquo;破烂儿&rdquo;都往回捡，老李却不以为意，本着&ldquo;厉行节约　反对浪费&rdquo;的原则，工作44年，精打细算地为单位节省100多万元。</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/130942212lht.jpg\" /></div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">视频</div>\r\n', '/Jeedw/u/cms/www/201510/130942212lht.jpg', '1', '1', '2015-10-13 09:42:34', '国网北京电力', '2015-10-