INSERT INTO `jc_microblog` VALUES ('139', '咱检修人员，才华也是棒棒哒', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国网北京检修公司</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">小伙伴们，&ldquo;用心共担杯&rdquo;三行诗大赛作品新鲜出炉啦！检修师傅们把日常、秋检工作等用诗句表达出来，抒发着他们对工作的追求，对电网的热爱！ps:小编mm告诉大家，想看更多作品可以关注&ldquo;皇城根下的检修青年&rdquo;微信公众号呦！</div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/15094916mx7f.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/15094928zjq2.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/150949506d3b.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/150950017in7.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/15095012t1qk.jpg\" /></p>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/15095028yet7.jpg\" /></p>\r\n', '/Jeedw/u/cms/www/201510/15095028yet7.jpg', '1', '1', '2015-10-15 09:50:39', '国网北京电力', '2015-10-15 09:50:39', '[图片]', '0', '0', '');
