INSERT INTO `jc_microblog` VALUES ('145', '重阳佳节尽孝心，掌上电力送贴心', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国网北京市电力公司朝阳供电公司</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">#重阳佳节尽孝心，掌上电力送贴心#@国网北京市电力公司<span class=\"Apple-converted-space\">&nbsp;</span>10月21日，正值重阳佳节。国家电网首都电力党员服务队朝阳分队走进朝阳区松榆西里社区，向20多位老人讲解智能电表用电常识，为前来咨询的居民绑定&ldquo;掌上电力&rdquo;客户端,实现轻轻一点用电尽在掌握之中，随时随地体验全新的&ldquo;拇指生活体验&rdquo;服务。</div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/21133413y932.jpg\" /></p>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">&nbsp;</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/21133