INSERT INTO `jc_microblog` VALUES ('156', '@国家电网: 光明工程 照亮您的二宝@国资小新', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国家电网上海市电力公司</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">10月30日，2015年上海市政府实事工程之一、老旧住宅小区电能计量表前供电设施维护和更新改造，即&ldquo;光明工程&rdquo;已完成3400个小区120万户，提前超额完成了年度实事工程任务。</div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201510/301507419txe.jpg\" /></p>\r\n', '/Jeedw/u/cms/www/201510/301507419txe.jpg', '1', '1', '2015-10-30 15:07:48', '国网北京电力', '2015-10-30 15:07:48', '[图片]', '0', '0', '');
