INSERT INTO `jc_microblog` VALUES ('160', '安全用电小常识', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国家电网报</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">【安全用电小常识】人们为了家居美观，装修时喜欢把塑料绝缘导线埋于墙内，但这有安全隐患。其外包塑料经长期使用会老化龟裂，绝缘水平大大降低；一旦墙体受潮，就会引起大面积漏电，危及人身；直接暗埋，不利于线路检修和保养。为了用电安全，可以选择将其隐秘在可开拉的门柜抽屉里。via天津城东供电</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/01165224u2js.jpg\" /></div>\r\n', '/Jeedw/u/cms/www/201511/01165224u2js.jpg', '1', '1', '2015-11-01 16:52:33', '国网北京电力', '2015-11-01 16:52:33', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('161', '“看漫画，说安全”，第二弹来啦！~~', '', '<div class=\"WB_info\" style=\"padding-bottom: 0px; text-transform: none; text-indent: 0px; margin: 0px 0px 3px; padding-left: 0px; padding-right: 0px; font: 11.81px/13.63px Arial, \'Microsoft YaHei\'; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 5px; -webkit-text-stroke-width: 0px\">@国网北京怀柔供电公司</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"padding-bottom: 3px; text-transform: none; text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 11.81px/23px Arial, \'Microsoft YaHei\'; word-wrap: break-word; white-space: normal; letter-spacing: normal; color: rgb(51,51,51); overflow: hidden; word-spacing: 0px; padding-top: 1px; -webkit-text-stroke-width: 0px\">#看漫画 说安全#第二弹：地线是保护我们工作安全可靠的有效手段，千万别让这条保护安全的地线变成我们工作的牵绊！！！<span class=\"Apple-converted-space\">&nbsp;</span>@国网北京市电力公司<span class=\"Apple-converted-space\">&nbsp;</span>@亮报<span class=\"Apple-converted-space\">&nbsp;</span>@国家电网<span class=\"Apple-converted-space\">&nbsp;</span>@国家电网报<span class=\"Apple-converted-space\">&nbsp;</span>@中国电力报<span class=\"Apple-converted-space\">&nbsp;</span>@国家电网杂志<span class=\"Apple-converted-space\">&nbsp;</span>@国家电网报手机报</div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/05093135q32a.jpg\" /></p>\r\n', '/Jeedw/u/cms/www/201511/031716102xzz.jpg', '1', '1', '2015-11-03 17:16:19', '国网北京电力', '2015-11-05 09:31:45', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('162', '家用电器耗电之“琅琊榜”', '', '<div style=\"padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 13.63px/23.63px Arial, \'Microsoft YaHei\'; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-width: 0px\">&nbsp;&nbsp; 各位亲，还记得&ldquo;智能用电 智慧生活&rdquo;活动么？国网北京电力上门为20户志愿家庭检测电器的耗电量，量身打造节电方案。小编今天为大家发福利，公布我们检测得到的&ldquo;耗电琅琊榜&rdquo;，再跟您说说节电窍门儿，为咱身边的绿水蓝天，还有咱自己的荷包做贡献。</div>\r\n<div style=\"padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 13.63px/23.63px Arial, \'Microsoft YaHei\'; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-width: 0px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"max-width: 100%; word-wrap: break-word !important; font-weight: bold\">耗电榜第一名 热水器</span></div>\r\n<div style=\"padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 13.63px/23.63px Arial, \'Microsoft YaHei\'; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-width: 0px\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/050935215w8s.jpg\" /></div>\r\n<p><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/05093808x5er.jpg\" /></p>\r\n<p>&nbsp;</p>\r\n<p style=\"padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px 0px 20px; padding-left: 0px; padding-right: 0px; font: 1