INSERT INTO `jc_microblog` VALUES ('167', '必备！京城电动汽车充电桩接电指南', '', '<p style=\"padding-bottom: 0px; text-transform: none; background-color: white; text-indent: 0px; margin: 0cm 0cm 0pt; padding-left: 0px; background-attachment: scroll; padding-right: 0px; font: 13.63px/23.63px Arial, \'Microsoft YaHei\'; max-width: 100%; word-wrap: break-word !important; white-space: normal; background-position: 0% 0%; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; background-size: auto; background-clip: border-box; -webkit-text-stroke-width: 0px; background-origin: padding-box\"><span style=\"background-color: white; background-attachment: scroll; font-family: 宋体; max-width: 100%; word-wrap: break-word !important; background-position: 0% 0%; color: rgb(62,62,62); background-size: auto; background-clip: border-box; background-origin: padding-box\"><span class=\"Apple-converted-space\">&nbsp;</span>听说参与本期个人新能源车指标申请的</span><span style=\"background-color: white; background-attachment: scroll; font-family: 微软雅黑, sans-serif; max-width: 100%; word-wrap: break-word !important; background-position: 0% 0%; color: rgb(62,62,62); background-size: auto; background-clip: border-box; background-origin: padding-box\">17150</span><span style=\"background-color: white; background-attachment: scroll; font-family: 宋体; max-width: 100%; word-wrap: break-word !important; background-position: 0% 0%; color: rgb(62,62,62); background-size: auto; background-clip: border-box; background-origin: padding-box\">名小伙伴，都获得了上牌资格，恭喜您啊！</span></p>\r\n<div style=\"padding-bottom: 0px; text-transform: none; background-color: rgb(255,255,255); text-indent: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 13.63px/23.63px Arial, \'Microsoft YaHei\'; max-width: 100%; word-wrap: break-word !important; white-space: normal; letter-spacing: normal; height: auto !important; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-stroke-widt