INSERT INTO `jc_microblog` VALUES ('238', '转发微博 ', '', '<div class=\"WB_info\">@国网北京密云供电公司</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\">#今天我在岗# 寂静小屋难觅处，此处风景独好！11月22日，国网北京密云供电公司运行保障人员冒雪对10千伏城李路、彩沙路开展线路巡视工作，实地检查少有人至的重点路段的设备状态。@国网北京市电力公司 @国家电网</div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/23103019won3.jpg\" /></div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/23103032lgzr.jpg\" /></div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/23103046wrke.jpg\" /></div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/23103104ahum.jpg\" /></div>\r\n<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/23103119syqe.jpg\" /></div>\r\n', '/Jeedw/u/cms/www/201511/23103119syqe.jpg', '1', '1', '2015-11-23 10:31:29', '国网北京电力', '2015-11-23 10:31:29', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('239', '“煤改电”用户让我看见你们的双手~! @国家电网 @国家电网报 @亮报 @国家电网报 ', '', '<div class=\"W_arrow_bor W_arrow_bor_t\">&nbsp;</div>\r\n<div class=\"WB_expand S_bg1\" node-type=\"feed_list_forwardContent\">\r\n	<div class=\"WB_info\">@国网北京通州供电公司</div>\r\n	<div class=\"WB_text\" node-type=\"feed_list_reason\">连续下了好几天雪，气象部门告诉我们还要再下好几天雪<img alt=\"[笑cry]\" render=\"ext\" src=\"http://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/34/xiaoku_org.gif\" style=\"visibility: visible\" title=\"[笑cry]\" type=\"face\" />在这样差评的天气里，您家暖气热么？&ldquo;煤改电&rdquo;用户让我看见你们的双手~至于啥是&ldquo;煤改电&rdquo;呢？电力复联的电暖队长要给我们讲两句儿~撒花<img alt=\"[hold住]\" render=\"ext\" src=\"http://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/05/lxhholdzhu_org.gif\" style=\"visibility: visible\" title=\"[hold住]\" type=\"face\" /><img alt=\"[玫瑰]\" render=\"ext\" src=\"http://img.t.sinajs.cn/t4/appstyle/expression/ext/normal/f6/lxhrose_org.gif\" style=\"visibility: visible\" title=\"[玫瑰]\" type=\"face\" />@国网北京市电力公司 @亮报 @国家电网杂志 @国家电网报 @国家电网</div>\r\n	<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/24103636ajsg.jpg\" /></div>\r\n	<div class=\"WB_text\" node-type=\"feed_list_reason\" style=\"text-align: center\"><img alt=\"\" src=\"/Jeedw/u/cms/www/201511/241036458wuf.jpg\" /></div>\r\n</div>\r\n<p>&nbsp;</p>\r\n', '/Jeedw/u/cms/www/201511/241036458wuf.jpg', '1', '1', '2015-11-24 10:36:53', '国网北京电力', '2015-11-24 10:36:53', '[图片]', '0', '0', '');
INSERT INTO `jc_microblog` VALUES ('240', '【视频：雪来了，该我们上了！】 ', '', '<p>2015年11月22日，北京遭遇暴雪天气，国网北京电力积极应对，去除电力杆塔覆冰，确保电网安全。雪来了，该我们上了！@国家电网 @国家电网报 @国家电网杂志 @亮报</p>\r\n', '', '1', '1', '2015-11-24 10:37:25', '国网北京电力', '2015-11-24 10:37:25', '', '0', '0', '');
